package org.mcsully.challenges;

import static org.junit.jupiter.api.Assertions.assertEquals;
import static org.junit.jupiter.api.Assertions.assertThrows;
import static org.junit.jupiter.api.Assertions.assertTrue;

import org.junit.jupiter.api.AfterAll;
import org.junit.jupiter.api.BeforeAll;
import org.junit.jupiter.api.Test;

import org.mcsully.challenges.Perfect_Subsequences;

/**
 * Unit test for simple Challenges.
 */
public class Perfect_SubsequencesTest
{
  private static String logGood = "All tests pass";
  private static String logFail = "There are test failures";

  @BeforeAll
  public static void printHeader(){
    System.out.println("======================= Perfect Subsequences Tests Starting =======================");
  }

  @Test
  public void testPerfect_Subsequences() {
    Perfect_Subsequences perSeq = new Perfect_Subsequences();
    try {
      perSeq.checkSequence( new String[] {"3", "abcd", "cad", "dcc"} );
    } catch (IllegalArgumentException e) {
      System.out.println( "\ntestOne: " + logFail + ": " + e.toString());
    }
  }

  @Test
  public void testOne() {
    Perfect_Subsequences perSeq = new Perfect_Subsequences();
    try {
      perSeq.checkSequence( new String[] {"1", "abacadbbcd"} );
    } catch (IllegalArgumentException e) {
      System.out.println( "\ntestOne: " + logFail + ": " + e.toString());
    }
  }

  @Test
  public void testLong() {
    Perfect_Subsequences perSeq = new Perfect_Subsequences();
    try {
      perSeq.checkSequence( new String[] {"2", 
"",
""} );
    } catch (IllegalArgumentException e) {
      System.out.println( "\ntestLong: " + logFail + ": " + e.toString());
    }
  }

  @Test
  public void testTooMany() {
    Perfect_Subsequences perSeq = new Perfect_Subsequences();
    try {
      perSeq.checkSequence( new String[] {"7", "a","b","a","c","a","d","b"} );
    } catch (IllegalArgumentException e) {
      System.out.println( "\ntestTooMany: " + logFail + ": " + e.toString());
    }
  }

  @Test
  public void testNegative() {
    Perfect_Subsequences perSeq = new Perfect_Subsequences();
    try {
      perSeq.checkSequence( new String[] {"1","-211"} );
    } catch (IllegalArgumentException e) {
      System.out.println( "\ntestNegative: " + logFail + ": " + e.toString());
    }
  }

  @Test
  public void testNull() {
    Perfect_Subsequences perSeq = new Perfect_Subsequences();
    try {
      perSeq.checkSequence( null );
    } catch (NullPointerException e) {
      System.out.println( "\ntestNull: " + logFail + ": " + e.toString());
    }
  }

  @AfterAll
  public static void printDivider(){
    System.out.println("======================= Perfect Subsequences Tests Completed =======================");
  }

}
